source.buildSpec:
{
  "version": "0.2",
  "env": {
    "shell": "bash"
  },
  "phases": {
    "pre_build": {
      "commands": [
        "echo $CODEBUILD_RESOLVED_SOURCE_VERSION",
        "npm install"
      ]
    },
    "build": {
      "commands": [
        "echo Build started on `date`",
        "npm run build"
      ]
    },
    "post_build": {
      "commands": [
        "echo Build completed on `date`"
      ]
    }
  },
  "artifacts": {
    "files": [
      "**/*"
    ],
    "base-directory": "build"
  }
}
